// REACT
import { useEffect, useState } from 'react';

// COMPONENTS
import ProjectCard from "../projectCard/ProjectCard";
import BtnSlider from "../btnSlider/BtnSlider";

// STYLE
import './slider.scss';

function Slider() {
    const [projects, setProjects] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch("https://firestore.googleapis.com/v1/projects/monportfolio-8dd08/databases/(default)/documents/works");
                const data = await response.json();
                const projectsFields = data.documents.map(doc => doc.fields);
                setProjects(projectsFields);
                console.log("fetchData", projectsFields);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        }
        fetchData();
    }, []);

    const getRotateY = (index, total) => {
        const middleIndex = Math.floor(total / 2);
        const middleRange = [middleIndex - 2, middleIndex - 1, middleIndex, middleIndex + 1, middleIndex + 2];
        if (index < middleRange[0]) {
          return -67.5;
        } else if (index > middleRange[4]) {
          return 67.5;
        } else {
          switch (index - (middleIndex - 2)) {
            case 0: return -45;
            case 1: return -22.5;
            case 2: return 0;
            case 3: return 22.5;
            case 4: return 45;
            default: return 0;
          }
        }
    };

    const getTranslateZ = (index, total) => {
        const middleIndex = Math.floor(total / 2);
        const middleRange = [middleIndex - 2, middleIndex - 1, middleIndex, middleIndex + 1, middleIndex + 2];
        if (index < middleRange[0] || index > middleRange[4]) {
          return 530;
        } else {
          switch (index - (middleIndex - 2)) {
            case 0: return 400;
            case 1: return 450;
            case 2: return 500;
            case 3: return 450;
            case 4: return 400;
            default: return 500;
          }
        }
    };

    const getOpacity = (index, total) => {
      const middleIndex = Math.floor(total / 2);
      const middleRange = [middleIndex - 2, middleIndex - 1, middleIndex, middleIndex + 1, middleIndex + 2];
      if (index < middleRange[0] || index > middleRange[4]) {
        return 0;
      } else {
        switch (index - (middleIndex - 2)) {
          case 0: return 0.7;
          case 1: return 0.85;
          case 2: return 1;
          case 3: return 0.85;
          case 4: return 0.7;
          default: return 1;
        }
      }
  };

    return (
        <section className='sliderContainer'>
            <div className='sliderContainer__slider'>
                {projects.map((project, index) => {
                    const rotateY = getRotateY(index, projects.length);
                    const translateZ = getTranslateZ(index, projects.length);
                    const opacity = getOpacity(index, projects.length);
                    const style = {
                        transform: `rotateY(${rotateY}deg) translateZ(${translateZ}px)`,
                        opacity: opacity
                    }
                    return <ProjectCard 
                      key={index} 
                      project={project} 
                      style={style} 

                      />
                })}
            </div>
            <div className='sliderContainer__btn'>
                <BtnSlider 
                    id="prevBTn" 
                    value="<" 
                />
                <BtnSlider 
                    id="nextBTn" 
                    value=">" 
                />
            </div>

        </section>
    )
}

export default Slider;
