// FIREBASE
import { storage } from '../firebaseConfig'; 

// REACT
import { useState, useEffect } from 'react';

// COMPONENTS
import ProjectCard from "../../components/projectCard/ProjectCard";

function Home() {
    const [projects, setProjects] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch("https://firestore.googleapis.com/v1/projects/monportfolio-8dd08/databases/(default)/documents/works");
                const data = await response.json();
                const projectsFields = data.documents.map(doc => doc.fields);
                // On obtient les URL des images
                const projectsWithImages = await Promise.all(projectsFields.map(async (project) => {
                    const imageRef = storage.refFromURL(project.image.stringValue);
                    const imageUrl = await imageRef.getDownloadURL();
                    return { ...project, image: { stringValue: imageUrl } };
                }));

                setProjects();
                console.log("fetchData", projectsWithImages);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        }
        fetchData();
    }, [])

    return (
        <main>
            <section className='sliderContainer'>
                <div className='sliderContainer__slider'>
                    {projects.map((project, index) => (
                        <ProjectCard key={index} project={project}/>
                    ))}
                </div>
            </section>
        </main>
    )
}

export default Home;
