// REACT
import { useEffect, useState } from 'react';

// COMPONENTS
import ProjectCard from "../../components/projectCard/ProjectCard";

// STYLE
import './home.scss';

function Home() {
    const [projects, setProjects] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch("https://firestore.googleapis.com/v1/projects/monportfolio-8dd08/databases/(default)/documents/works");
                const data = await response.json();
                const projectsFields = data.documents.map(doc => doc.fields);
                setProjects(projectsFields);
                console.log("fetchData", projectsFields);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        }
        fetchData();
    }, []);

    const getRotation = (index, total) => {
        const middleIndex = Math.floor(total / 2);
        const middleRange = [middleIndex - 2, middleIndex - 1, middleIndex, middleIndex + 1, middleIndex + 2];
        if (index < middleRange[0]) {
            const lowerDeg = -67.5;
          return (lowerDeg);
        } else if (index > middleRange[4]) {
            const superiorDeg = 67.5;
          return (superiorDeg);
        } else {
          switch (index - (middleIndex - 2)) {
            case 0: 
                const deg0 = -45;
                return (deg0);
            case 1: 
                const deg1 = -22.5;
                return (deg1);
            case 2: 
                const deg2 = 0;
                return (deg2);
            case 3: 
                const deg3 = 22.5;
                return (deg3);
            case 4: 
                const deg4 = 45;
                return (deg4);
            default: 
                const defaultDeg = 0;
                return (defaultDeg);
          }
        }
      };

    return (
        <main>
            <section className='sliderContainer'>
                <div className='sliderContainer__slider'>
                    {projects.map((project, index) => {
                        const rotateY = getRotation(index, projects.length);
                        const translateZ = getRotation(index,projects.length);
                        const style = {
                            transform: `rotateY(${rotateY}deg) translateZ(${translateZ}px)`
                        }
                        return <ProjectCard key={index} project={project} style={style}/>
                    })}
                </div>
            </section>
        </main>
    )
}

export default Home;
